PREFIX owl: <http://www.w3.org/2002/07/owl#>
 PREFIX wdt:  <http://www.wikidata.org/prop/direct/>
 PREFIX wd:  <http://www.wikidata.org/entity/>
 PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
 PREFIX skos: <http://www.w3.org/2004/02/skos/core#>

 SELECT ?item ?itemLabel ?wdItem ?wdItemlabel ?wdPoliticalParty ?wdPoliticalPartyLabel WHERE {
   ?item skos:hasTopConcept 'PERSON'.
   ?item owl:sameAs ?wdItem.
   ?item rdfs:label ?itemLabel.

   SERVICE <https://query.wikidata.org/sparql> {
     ?wdItem rdfs:label ?wdItemlabel.
     FILTER(LANG(?wdItemlabel) = "en").
     OPTIONAL{ 
       ?wdItem wdt:P102 ?wdPoliticalParty.
       ?wdPoliticalParty rdfs:label ?wdPoliticalPartyLabel.
       FILTER(LANG(?wdPoliticalPartyLabel) = "en").
     }.
   } 

 } LIMIT 200

PREFIX owl: <http://www.w3.org/2002/07/owl#>
 PREFIX wdt:  <http://www.wikidata.org/prop/direct/>
 PREFIX wd:  <http://www.wikidata.org/entity/>
 PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
 PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
 PREFIX sdo: <https://schema.org/>
 SELECT * WHERE {
   ?item skos:hasTopConcept "PERSON".
   ?item rdfs:label ?itemLabel.
   ?item owl:sameAs ?wdItem.

   SERVICE <https://query.wikidata.org/sparql> {    
     #OPTIONAL{ ?wdItem wdt:P18 ?thumbnail }.
     # V&A 
     OPTIONAL {
      VALUES ?vaIDs { wdt:P3929 wdt:P6764  }.
      ?wdItem ?vaIDs ?vaID.
     }

     # Tate 
     OPTIONAL {
       VALUES ?tateIDs {wdt:P2741}.
       ?wdItem ?tateIDs ?tateID.
     }

     # BFI
     OPTIONAL {
       VALUES ?bfiIDs {wdt:P2703 wdt:P4326 wdt:P4438}.
       ?wdItem ?bfiIDs ?bfiID.
     }

     # British Museum
     OPTIONAL {
       VALUES ?bmIDs {wdt:P8565 wdt:P1711}.
       ?wdItem ?bmIDs ?britishMuseumID.
       }

     # National Portrait Gallery
     OPTIONAL {
       VALUES ?npgIDs {wdt:P1816 wdt:P9144}
       ?wdItem ?npgIDs ?nationalportraitID.
       }
   } 
 }

PREFIX hc: <http://www.heritageconnector.org/RDF/>

SELECT * WHERE {
  {SELECT DISTINCT ?p WHERE{
     # add one of PERSON, ORG, OBJECT, DATE, NORP, FAC, LOC, LANGUAGE, EVENT to the end of the URL below
     FILTER(STRSTARTS(STR(?p), "http://www.heritageconnector.org/RDF/entity")).
     ?s ?p ?o
    }
  }
  ?subject ?p ?entity 
  # this line returns all non-linked (non-URL) entities
  #FILTER(!STRSTARTS(STR(?entity), "http")).
  # OR, this line returns all linked (URL) entities
  FILTER(STRSTARTS(STR(?entity), "http")).

# use the LIMIT and OFFSET numbers below to control page size and page number
} ORDER BY ?entity LIMIT 500 OFFSET 1000

PREFIX hc: <http://www.heritageconnector.org/RDF/>

SELECT ?p ?isLinked (COUNT(?entity) AS ?entityCount)  WHERE {
  {SELECT DISTINCT ?p WHERE{
     FILTER(STRSTARTS(STR(?p), "http://www.heritageconnector.org/RDF/entity")).
     ?s ?p ?o
    }
  }
  ?subject ?p ?entity.
  BIND(STRSTARTS(STR(?entity), "http") AS ?isLinked).

} GROUP BY ?p ?isLinked ORDER BY ?p ?isLinked
